-- Create profiles table for user information and roles
create table profiles (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users on delete cascade not null unique,
  email text not null,
  full_name text,
  role text not null default 'worker' check (role in ('admin', 'worker')),
  is_active boolean default true,
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table profiles enable row level security;

-- RLS Policies for profiles
-- Users can view their own profile
create policy "Users can view their own profile"
  on profiles for select
  using (auth.uid() = user_id);

-- Admins can view all profiles
create policy "Admins can view all profiles"
  on profiles for select
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Users can update their own profile (except role)
create policy "Users can update their own profile"
  on profiles for update
  using (auth.uid() = user_id)
  with check (auth.uid() = user_id and role = (select role from profiles where user_id = auth.uid()));

-- Admins can update any profile
create policy "Admins can update any profile"
  on profiles for update
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Admins can insert profiles
create policy "Admins can insert profiles"
  on profiles for insert
  with check (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Create index for faster lookups
create index profiles_user_id_idx on profiles(user_id);
create index profiles_role_idx on profiles(role);

-- Create function to automatically create profile on user signup
create or replace function public.handle_new_user()
returns trigger as $$
begin
  insert into public.profiles (user_id, email, full_name, role)
  values (
    new.id,
    new.email,
    coalesce(new.raw_user_meta_data->>'full_name', ''),
    coalesce(new.raw_user_meta_data->>'role', 'worker')
  );
  return new;
end;
$$ language plpgsql security definer;

-- Trigger to create profile on signup
create trigger on_auth_user_created
  after insert on auth.users
  for each row execute procedure public.handle_new_user();

-- Function to update updated_at timestamp
create or replace function update_updated_at_column()
returns trigger as $$
begin
  new.updated_at = now();
  return new;
end;
$$ language plpgsql;

-- Trigger to update updated_at on profile changes
create trigger update_profiles_updated_at
  before update on profiles
  for each row execute procedure update_updated_at_column();

-- Create orders table
create table orders (
  id uuid primary key default gen_random_uuid(),
  order_number text not null unique,
  customer_name text not null,
  customer_phone text,
  customer_address text,
  status text not null default 'pending' check (status in ('pending', 'preparing', 'ready', 'delivered', 'cancelled')),
  total_amount decimal(10, 2) default 0,
  paid_amount decimal(10, 2) default 0,
  notes text,
  source text default 'manual' check (source in ('manual', 'whatsapp')),
  whatsapp_message_id text,
  is_read boolean default false,
  created_by uuid references auth.users,
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table orders enable row level security;

-- RLS Policies for orders
-- Authenticated users can view all orders
create policy "Authenticated users can view orders"
  on orders for select
  using (auth.role() = 'authenticated');

-- Authenticated users can insert orders
create policy "Authenticated users can insert orders"
  on orders for insert
  with check (auth.role() = 'authenticated');

-- Authenticated users can update orders
create policy "Authenticated users can update orders"
  on orders for update
  using (auth.role() = 'authenticated');

-- Only admins can delete orders
create policy "Admins can delete orders"
  on orders for delete
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Create indexes for faster queries
create index orders_order_number_idx on orders(order_number);
create index orders_status_idx on orders(status);
create index orders_customer_phone_idx on orders(customer_phone);
create index orders_created_at_idx on orders(created_at desc);
create index orders_is_read_idx on orders(is_read);

-- Trigger to update updated_at
create trigger update_orders_updated_at
  before update on orders
  for each row execute procedure update_updated_at_column();

-- Function to generate order number
create or replace function generate_order_number()
returns text as $$
declare
  new_number text;
  counter int;
begin
  -- Get today's date in YYYYMMDD format
  new_number := to_char(now(), 'YYYYMMDD');

  -- Count orders created today
  select count(*) + 1 into counter
  from orders
  where order_number like new_number || '%';

  -- Append counter with leading zeros
  new_number := new_number || '-' || lpad(counter::text, 4, '0');

  return new_number;
end;
$$ language plpgsql;

-- Trigger to auto-generate order number if not provided
create or replace function set_order_number()
returns trigger as $$
begin
  if new.order_number is null or new.order_number = '' then
    new.order_number := generate_order_number();
  end if;
  return new;
end;
$$ language plpgsql;

create trigger set_order_number_trigger
  before insert on orders
  for each row execute procedure set_order_number();

-- Create whatsapp_config table
create table whatsapp_config (
  id uuid primary key default gen_random_uuid(),
  verify_token text,
  access_token text,
  phone_number_id text,
  webhook_url text,
  is_active boolean default false,
  auto_reply_enabled boolean default true,
  auto_reply_message text default 'Gracias por tu pedido. Lo hemos recibido y lo procesaremos pronto.',
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table whatsapp_config enable row level security;

-- RLS Policies for whatsapp_config
-- Only admins can view whatsapp config
create policy "Admins can view whatsapp config"
  on whatsapp_config for select
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Only admins can insert whatsapp config
create policy "Admins can insert whatsapp config"
  on whatsapp_config for insert
  with check (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Only admins can update whatsapp config
create policy "Admins can update whatsapp config"
  on whatsapp_config for update
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Only admins can delete whatsapp config
create policy "Admins can delete whatsapp config"
  on whatsapp_config for delete
  using (
    exists (
      select 1 from profiles
      where user_id = auth.uid() and role = 'admin'
    )
  );

-- Trigger to update updated_at
create trigger update_whatsapp_config_updated_at
  before update on whatsapp_config
  for each row execute procedure update_updated_at_column();

-- Create notifications table
create table notifications (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users on delete cascade,
  title text not null,
  message text not null,
  type text not null default 'info' check (type in ('info', 'success', 'warning', 'error', 'order')),
  related_order_id uuid references orders on delete cascade,
  is_read boolean default false,
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table notifications enable row level security;

-- RLS Policies for notifications
-- Users can view their own notifications or all if no user_id (broadcast)
create policy "Users can view their notifications"
  on notifications for select
  using (user_id is null or auth.uid() = user_id);

-- Authenticated users can insert notifications
create policy "Authenticated users can insert notifications"
  on notifications for insert
  with check (auth.role() = 'authenticated');

-- Users can update their own notifications
create policy "Users can update their own notifications"
  on notifications for update
  using (auth.uid() = user_id);

-- Users can delete their own notifications
create policy "Users can delete their own notifications"
  on notifications for delete
  using (auth.uid() = user_id);

-- Create indexes
create index notifications_user_id_idx on notifications(user_id);
create index notifications_is_read_idx on notifications(is_read);
create index notifications_created_at_idx on notifications(created_at desc);
create index notifications_related_order_id_idx on notifications(related_order_id);

-- Function to create notification for new orders
create or replace function notify_new_order()
returns trigger as $$
begin
  -- Create notification for all users (broadcast)
  insert into notifications (title, message, type, related_order_id)
  values (
    'Nuevo Pedido',
    'Pedido #' || new.order_number || ' de ' || new.customer_name,
    'order',
    new.id
  );

  return new;
end;
$$ language plpgsql;

create trigger notify_new_order_trigger
  after insert on orders
  for each row execute procedure notify_new_order();

-- Function to create notification for order status changes
create or replace function notify_order_status_change()
returns trigger as $$
declare
  status_text text;
begin
  if old.status != new.status then
    case new.status
      when 'pending' then status_text := 'Pendiente';
      when 'preparing' then status_text := 'En Preparación';
      when 'ready' then status_text := 'Listo';
      when 'delivered' then status_text := 'Entregado';
      when 'cancelled' then status_text := 'Cancelado';
      else status_text := new.status;
    end case;

    insert into notifications (title, message, type, related_order_id)
    values (
      'Estado Actualizado',
      'Pedido #' || new.order_number || ' ahora está ' || status_text,
      'info',
      new.id
    );
  end if;

  return new;
end;
$$ language plpgsql;

create trigger notify_order_status_change_trigger
  after update on orders
  for each row execute procedure notify_order_status_change();

