import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

/**
 * Parses a quantity value from a string, handling decimals and fractions.
 */
function parseQuantityValue(quantityStr: string): number {
  if (!quantityStr) {
    throw new Error('Quantity string is empty');
  }

  // Handle fractions (e.g., "1/2", "3/4")
  if (quantityStr.includes('/')) {
    const parts = quantityStr.split('/');
    if (parts.length !== 2) {
      throw new Error(`Invalid fraction format: ${quantityStr}`);
    }
    const numerator = parseInt(parts[0].trim(), 10);
    const denominator = parseInt(parts[1].trim(), 10);

    if (isNaN(numerator) || isNaN(denominator) || denominator === 0) {
      throw new Error(`Invalid fraction values: ${quantityStr}`);
    }

    return numerator / denominator;
  }

  // Handle decimals and whole numbers
  const value = parseFloat(quantityStr);
  if (isNaN(value)) {
    throw new Error(`Invalid number format: ${quantityStr}`);
  }

  return value;
}

/**
 * Normalizes a unit string to a standard format.
 */
function normalizeUnit(unit: string): string {
  if (!unit) {
    return 'unit';
  }

  const normalized = unit.toLowerCase().trim();

  // Weight units
  if (normalized === 'kg' || normalized === 'kgs' || normalized === 'kilo' || normalized === 'kilos' || normalized === 'kilogramo' || normalized === 'kilogramos') {
    return 'kg';
  }
  if (normalized === 'g' || normalized === 'gs' || normalized === 'gramo' || normalized === 'gramos') {
    return 'g';
  }

  // Volume units
  if (normalized === 'l' || normalized === 'ls' || normalized === 'litro' || normalized === 'litros') {
    return 'l';
  }
  if (normalized === 'ml' || normalized === 'mls' || normalized === 'mililitro' || normalized === 'mililitros') {
    return 'ml';
  }

  // Count units
  if (normalized === 'unit' || normalized === 'units' || normalized === 'unidad' || normalized === 'unidades' || normalized === 'u' || normalized === 'pza' || normalized === 'pzas' || normalized === 'pieza' || normalized === 'piezas') {
    return 'unit';
  }

  // Default to unit if unknown
  return 'unit';
}

interface ParsedOrderItem {
  quantity: number;
  unit: string;
  product: string;
}

/**
 * Parses a single line from the WhatsApp message.
 */
function parseLine(line: string): ParsedOrderItem {
  const trimmedLine = line.trim();

  if (!trimmedLine) {
    throw new Error('Line is empty');
  }

  // Split by whitespace
  const parts = trimmedLine.split(/\s+/);

  if (parts.length < 3) {
    throw new Error(`Line has insufficient parts: ${trimmedLine}`);
  }

  // First part is quantity
  const quantityStr = parts[0];

  // Second part is unit
  const unitStr = parts[1];

  // Remaining parts are the product name
  const productParts = parts.slice(2);
  const product = productParts.join(' ').trim();

  if (!product) {
    throw new Error(`No product name found in line: ${trimmedLine}`);
  }

  try {
    const quantity = parseQuantityValue(quantityStr);
    const unit = normalizeUnit(unitStr);

    return {
      quantity,
      unit,
      product,
    };
  } catch (error) {
    throw new Error(`Failed to parse line "${trimmedLine}": ${error.message}`);
  }
}

/**
 * Parses a WhatsApp message into a list of order items.
 */
function parseWhatsAppMessage(message: string): ParsedOrderItem[] {
  if (!message || !message.trim()) {
    console.error('Empty message provided');
    return [];
  }

  const lines = message.split('\n');
  const orderItems: ParsedOrderItem[] = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Skip empty lines
    if (!line) {
      continue;
    }

    try {
      const parsedItem = parseLine(line);
      orderItems.push(parsedItem);
      console.log(`Successfully parsed line ${i + 1}:`, parsedItem);
    } catch (error) {
      console.error(`Failed to parse line ${i + 1} ("${line}"):`, error.message);
      // Continue to next line instead of stopping
    }
  }

  return orderItems;
}

/**
 * Parses order from WhatsApp message with new parsing logic
 */
function parseOrderFromMessage(message: string, phone: string) {
  console.log('Parsing message:', message);

  const parsedItems = parseWhatsAppMessage(message);

  if (parsedItems.length === 0) {
    console.log('No items could be parsed from message');
    // Create a fallback order with the raw message
    return {
      customerName: phone,
      customerPhone: phone,
      items: [{
        product_name: 'Pedido por WhatsApp',
        quantity: 1,
        unit_price: 0,
        notes: message,
      }],
      notes: 'Pedido no pudo ser parseado automáticamente',
    };
  }

  // Convert parsed items to order items format
  const items = parsedItems.map(item => ({
    product_name: `${item.product} (${item.quantity} ${item.unit})`,
    quantity: item.quantity,
    unit_price: 0, // Price needs to be set manually
    notes: `Unidad: ${item.unit}`,
  }));

  return {
    customerName: phone,
    customerPhone: phone,
    items,
    notes: undefined,
  };
}

async function sendWhatsAppMessage(
  phoneNumberId: string,
  accessToken: string,
  to: string,
  message: string
) {
  const url = `https://graph.facebook.com/v17.0/${phoneNumberId}/messages`;

  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      messaging_product: 'whatsapp',
      to,
      text: { body: message },
    }),
  });

  if (!response.ok) {
    const error = await response.text();
    console.error('Failed to send WhatsApp message:', error);
    throw new Error(`Failed to send message: ${error}`);
  }

  return response.json();
}

serve(async (req) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseUrl = Deno.env.get('SUPABASE_URL');
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY');
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // GET request - webhook verification
    if (req.method === 'GET') {
      const url = new URL(req.url);
      const mode = url.searchParams.get('hub.mode');
      const token = url.searchParams.get('hub.verify_token');
      const challenge = url.searchParams.get('hub.challenge');

      console.log('Webhook verification request:', { mode, token });

      // Get verify token from database
      const { data: config } = await supabase
        .from('whatsapp_config')
        .select('verify_token')
        .single();

      if (mode === 'subscribe' && token === config?.verify_token) {
        console.log('Webhook verified successfully');
        return new Response(challenge, {
          headers: { ...corsHeaders, 'Content-Type': 'text/plain' },
        });
      }

      return new Response('Forbidden', { status: 403, headers: corsHeaders });
    }

    // POST request - incoming message
    if (req.method === 'POST') {
      const body = await req.json();
      console.log('Received webhook:', JSON.stringify(body, null, 2));

      // Get WhatsApp config
      const { data: config } = await supabase
        .from('whatsapp_config')
        .select('*')
        .eq('is_active', true)
        .single();

      if (!config) {
        console.log('WhatsApp integration not configured or inactive');
        return new Response(
          JSON.stringify({ success: false, error: 'Not configured' }),
          { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
        );
      }

      // Process webhook entries
      const entry = body.entry?.[0];
      if (!entry) {
        return new Response(JSON.stringify({ success: true }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        });
      }

      const changes = entry.changes?.[0];
      const messages = changes?.value?.messages;

      if (!messages || messages.length === 0) {
        return new Response(JSON.stringify({ success: true }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        });
      }

      // Process each message
      for (const message of messages) {
        if (message.type !== 'text') continue;

        const messageText = message.text.body;
        const customerPhone = message.from;
        const messageId = message.id;

        console.log('Processing message from:', customerPhone);

        // Parse order from message using new parsing logic
        const parsedOrder = parseOrderFromMessage(messageText, customerPhone);

        if (!parsedOrder) {
          console.log('Could not parse order from message');
          continue;
        }

        // Create order in database
        const { data: order, error: orderError } = await supabase
          .from('orders')
          .insert({
            customer_name: parsedOrder.customerName,
            customer_phone: parsedOrder.customerPhone,
            status: 'pending',
            source: 'whatsapp',
            whatsapp_message_id: messageId,
            notes: parsedOrder.notes,
          })
          .select()
          .single();

        if (orderError) {
          console.error('Error creating order:', orderError);
          continue;
        }

        console.log('Created order:', order.id);

        // Create order items
        const orderItems = parsedOrder.items.map((item) => ({
          order_id: order.id,
          product_name: item.product_name,
          quantity: item.quantity,
          unit_price: item.unit_price,
          notes: item.notes,
        }));

        const { error: itemsError } = await supabase
          .from('order_items')
          .insert(orderItems);

        if (itemsError) {
          console.error('Error creating order items:', itemsError);
        }

        // Send auto-reply if enabled
        if (
          config.auto_reply_enabled &&
          config.access_token &&
          config.phone_number_id
        ) {
          try {
            const replyMessage = config.auto_reply_message.replace(
              '{order_number}',
              order.order_number
            );
            await sendWhatsAppMessage(
              config.phone_number_id,
              config.access_token,
              customerPhone,
              replyMessage
            );
            console.log('Sent auto-reply to:', customerPhone);
          } catch (error) {
            console.error('Error sending auto-reply:', error);
          }
        }
      }

      return new Response(JSON.stringify({ success: true }), {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      });
    }

    return new Response('Method not allowed', {
      status: 405,
      headers: corsHeaders,
    });
  } catch (error) {
    console.error('Error processing webhook:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type'
};

/**
 * Map of Spanish number words to their numeric values
 */
const NUMBER_WORDS: Record<string, number> = {
  'un': 1, 'uno': 1, 'una': 1,
  'dos': 2,
  'tres': 3,
  'cuatro': 4,
  'cinco': 5,
  'seis': 6,
  'siete': 7,
  'ocho': 8,
  'nueve': 9,
  'diez': 10,
  'once': 11,
  'doce': 12,
  'trece': 13,
  'catorce': 14,
  'quince': 15,
  'dieciséis': 16, 'dieciseis': 16,
  'diecisiete': 17,
  'dieciocho': 18,
  'diecinueve': 19,
  'veinte': 20,
  'veintiuno': 21,
  'veintidós': 22, 'veintidos': 22,
  'veintitrés': 23, 'veintitres': 23,
  'veinticuatro': 24,
  'veinticinco': 25,
  'treinta': 30,
  'cuarenta': 40,
  'cincuenta': 50,
};

/**
 * Known unit variations and their categories
 */
const UNIT_VARIATIONS: Record<string, string[]> = {
  'kilo': ['kilo', 'kilos', 'kg', 'kgs', 'k'],
  'gramo': ['gramo', 'gramos', 'gr', 'grs', 'g'],
  'unidad': ['unidad', 'unidades', 'u'],
  'malla': ['malla', 'mallas'],
  'saco': ['saco', 'sacos'],
  'cajón': ['cajón', 'cajon', 'cajones'],
  'bolsa': ['bolsa', 'bolsas'],
  'paquete': ['paquete', 'paquetes', 'pqte', 'pqtes'],
  'caja': ['caja', 'cajas'],
  'atado': ['atado', 'atados'],
  'racimo': ['racimo', 'racimos'],
  'cabeza': ['cabeza', 'cabezas'],
};

interface ParsedOrderItem {
  quantity: number;
  unit: string;
  product: string;
}

/**
 * Converts a number word to its numeric value
 */
function convertNumberWord(word: string): number | null {
  const normalized = word.toLowerCase().trim();
  return NUMBER_WORDS[normalized] ?? null;
}

/**
 * Parses a quantity value from a string
 */
function parseQuantityValue(quantityStr: string): number {
  if (!quantityStr || !quantityStr.trim()) {
    return 0;
  }

  const trimmed = quantityStr.trim();

  // Try as fraction
  if (trimmed.includes('/')) {
    const parts = trimmed.split('/');
    if (parts.length === 2) {
      const numerator = parseFloat(parts[0].trim());
      const denominator = parseFloat(parts[1].trim());

      if (!isNaN(numerator) && !isNaN(denominator) && denominator !== 0) {
        return numerator / denominator;
      }
    }
  }

  // Try as number
  const numValue = parseFloat(trimmed);
  if (!isNaN(numValue)) {
    return numValue;
  }

  // Try as text number
  const textValue = convertNumberWord(trimmed);
  if (textValue !== null) {
    return textValue;
  }

  console.warn(`Could not parse quantity: "${quantityStr}"`);
  return 0;
}

/**
 * Checks if a word is a known unit
 */
function isKnownUnit(word: string): boolean {
  if (!word) return false;

  const normalized = word.toLowerCase().trim();

  for (const variations of Object.values(UNIT_VARIATIONS)) {
    if (variations.includes(normalized)) {
      return true;
    }
  }

  return false;
}

/**
 * Normalizes a unit to its standard form
 */
function normalizeUnit(unit: string, quantity: number = 1): string {
  if (!unit || !unit.trim()) {
    return quantity === 1 ? 'unidad' : 'unidades';
  }

  const normalized = unit.toLowerCase().trim();

  for (const [standardUnit, variations] of Object.entries(UNIT_VARIATIONS)) {
    if (variations.includes(normalized)) {
      if (quantity === 1) {
        return standardUnit;
      } else {
        if (standardUnit === 'cajón') return 'cajones';
        if (standardUnit.endsWith('z')) return standardUnit.slice(0, -1) + 'ces';
        return standardUnit + 's';
      }
    }
  }

  return quantity === 1 ? 'unidad' : 'unidades';
}

/**
 * Parses a single segment from the WhatsApp message
 */
function parseSegment(segment: string): ParsedOrderItem | null {
  const trimmed = segment.trim();

  if (!trimmed) {
    return null;
  }

  const cleaned = trimmed.replace(/^[-•*]\s*/, '').trim();

  if (!cleaned) {
    return null;
  }

  // Pattern 1: Cantidad + Unidad + "de" + Producto
  let match = cleaned.match(/^(\d+(?:\/\d+)?|\w+)\s+(\w+)\s+de\s+(.+)$/i);
  if (match) {
    const quantity = parseQuantityValue(match[1]);
    const unit = normalizeUnit(match[2], quantity);
    const product = match[3].trim();

    if (quantity > 0 && product) {
      return { quantity, unit, product };
    }
  }

  // Pattern 2: Cantidad + Unidad (sin espacio) + "de" + Producto
  match = cleaned.match(/^(\d+(?:\/\d+)?)([a-zA-Z]+)\s+de\s+(.+)$/i);
  if (match) {
    const quantity = parseQuantityValue(match[1]);
    const unit = normalizeUnit(match[2], quantity);
    const product = match[3].trim();

    if (quantity > 0 && product) {
      return { quantity, unit, product };
    }
  }

  // Pattern 3: Cantidad + Unidad (sin espacio) + Producto
  match = cleaned.match(/^(\d+(?:\/\d+)?)([a-zA-Z]+)\s+(.+)$/i);
  if (match) {
    const potentialUnit = match[2];
    if (isKnownUnit(potentialUnit)) {
      const quantity = parseQuantityValue(match[1]);
      const unit = normalizeUnit(potentialUnit, quantity);
      const product = match[3].trim();

      if (quantity > 0 && product) {
        return { quantity, unit, product };
      }
    }
  }

  // Pattern 4: Cantidad + Unidad + Producto (con espacio)
  match = cleaned.match(/^(\d+(?:\/\d+)?|\w+)\s+(\w+)\s+(.+)$/i);
  if (match) {
    const potentialUnit = match[2];
    if (isKnownUnit(potentialUnit)) {
      const quantity = parseQuantityValue(match[1]);
      const unit = normalizeUnit(potentialUnit, quantity);
      const product = match[3].trim();

      if (quantity > 0 && product) {
        return { quantity, unit, product };
      }
    }
  }

  // Pattern 5: Cantidad + Producto (sin unidad explícita)
  match = cleaned.match(/^(\d+(?:\/\d+)?|\w+)\s+(.+)$/i);
  if (match) {
    const quantity = parseQuantityValue(match[1]);
    const product = match[2].trim();

    if (quantity > 0 && product && !isKnownUnit(product.split(/\s+/)[0])) {
      const unit = normalizeUnit('', quantity);
      return { quantity, unit, product };
    }
  }

  // Pattern 6: Producto + Cantidad + Unidad
  match = cleaned.match(/^(.+?)\s+(\d+(?:\/\d+)?|\w+)\s*([a-zA-Z]*)$/i);
  if (match) {
    const product = match[1].trim();
    const quantityStr = match[2];
    const unitStr = match[3];

    const quantity = parseQuantityValue(quantityStr);

    if (quantity > 0 && product) {
      if (unitStr && isKnownUnit(unitStr)) {
        const unit = normalizeUnit(unitStr, quantity);
        return { quantity, unit, product };
      } else {
        const quantityMatch = quantityStr.match(/^(\d+(?:\/\d+)?)([a-zA-Z]+)$/);
        if (quantityMatch && isKnownUnit(quantityMatch[2])) {
          const qty = parseQuantityValue(quantityMatch[1]);
          const unit = normalizeUnit(quantityMatch[2], qty);
          return { quantity: qty, unit, product };
        } else {
          const unit = normalizeUnit('', quantity);
          return { quantity, unit, product };
        }
      }
    }
  }

  // Pattern 7: Fracción + "de" + Producto
  match = cleaned.match(/^(\d+\/\d+)\s+de\s+(.+)$/i);
  if (match) {
    const quantity = parseQuantityValue(match[1]);
    const product = match[2].trim();

    if (quantity > 0 && product) {
      const unit = normalizeUnit('', quantity);
      return { quantity, unit, product };
    }
  }

  console.warn(`Could not parse segment: "${cleaned}"`);
  return null;
}

/**
 * Splits a line into multiple segments
 */
function splitLineIntoSegments(line: string): string[] {
  const trimmed = line.trim();

  if (!trimmed) {
    return [];
  }

  // Split by commas
  if (trimmed.includes(',')) {
    return trimmed.split(',').map(s => s.trim()).filter(s => s.length > 0);
  }

  // Try to detect multiple items
  const segments: string[] = [];
  let currentSegment = '';
  const words = trimmed.split(/\s+/);

  for (let i = 0; i < words.length; i++) {
    const word = words[i];

    const isQuantityStart = /^\d+(?:\/\d+)?[a-zA-Z]*$/.test(word) || convertNumberWord(word) !== null;

    if (isQuantityStart && currentSegment.trim().length > 0) {
      segments.push(currentSegment.trim());
      currentSegment = word;
    } else {
      currentSegment += (currentSegment ? ' ' : '') + word;
    }
  }

  if (currentSegment.trim().length > 0) {
    segments.push(currentSegment.trim());
  }

  if (segments.length === 1) {
    return [trimmed];
  }

  const validSegments = segments.filter(seg => {
    const parsed = parseSegment(seg);
    return parsed !== null;
  });

  if (validSegments.length === 0) {
    return [trimmed];
  }

  return segments;
}

/**
 * Parses a WhatsApp message into a list of order items
 */
function parseWhatsAppMessage(message: string): ParsedOrderItem[] {
  if (!message || !message.trim()) {
    console.warn('Empty message provided');
    return [];
  }

  const lines = message.split('\n');
  const orderItems: ParsedOrderItem[] = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    if (!line) {
      continue;
    }

    const segments = splitLineIntoSegments(line);

    for (const segment of segments) {
      try {
        const parsedItem = parseSegment(segment);

        if (parsedItem) {
          orderItems.push(parsedItem);
          console.log(`✓ Parsed: "${segment}" →`, parsedItem);
        } else {
          console.warn(`✗ Could not parse: "${segment}"`);
        }
      } catch (error) {
        console.error(`✗ Error parsing segment "${segment}":`, error);
      }
    }
  }

  console.log(`Parsed ${orderItems.length} items from ${lines.length} lines`);
  return orderItems;
}

/**
 * Parses order from WhatsApp message
 */
function parseOrderFromMessage(message: string, phone: string) {
  console.log('Parsing message:', message);
  const parsedItems = parseWhatsAppMessage(message);

  if (parsedItems.length === 0) {
    console.log('No items could be parsed from message');
    return {
      customerName: phone,
      customerPhone: phone,
      items: [
        {
          product_name: 'Pedido por WhatsApp',
          quantity: 1,
          unit_price: 0,
          notes: message
        }
      ],
      notes: 'Pedido no pudo ser parseado automáticamente'
    };
  }

  const items = parsedItems.map((item) => ({
    product_name: `${item.product} (${item.quantity} ${item.unit})`,
    quantity: item.quantity,
    unit_price: 0,
    notes: `Unidad: ${item.unit}`
  }));

  return {
    customerName: phone,
    customerPhone: phone,
    items,
    notes: undefined
  };
}

async function sendWhatsAppMessage(
