import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type'
};

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const { access_token, phone_number_id, webhook_url } = await req.json();

    if (!access_token || !phone_number_id) {
      return new Response(
        JSON.stringify({
          success: false,
          message: 'Access token y Phone Number ID son requeridos'
        }),
        {
          status: 400,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        }
      );
    }

    // Test 1: Verify the access token by making a request to WhatsApp API
    console.log('Testing WhatsApp API connection...');
    const whatsappApiUrl = `https://graph.facebook.com/v17.0/${phone_number_id}`;

    const whatsappResponse = await fetch(whatsappApiUrl, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${access_token}`,
      },
    });

    if (!whatsappResponse.ok) {
      const errorText = await whatsappResponse.text();
      console.error('WhatsApp API error:', errorText);

      return new Response(
        JSON.stringify({
          success: false,
          message: `Error al conectar con WhatsApp API: ${whatsappResponse.status} - ${errorText}`
        }),
        {
          status: 200,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        }
      );
    }

    const whatsappData = await whatsappResponse.json();
    console.log('WhatsApp API response:', whatsappData);

    // Test 2: Verify webhook URL is accessible (if provided)
    let webhookStatus = 'No verificado';
    if (webhook_url) {
      try {
        console.log('Testing webhook URL accessibility...');
        const webhookResponse = await fetch(webhook_url, {
          method: 'GET',
          headers: {
            'User-Agent': 'Supabase-WhatsApp-Test/1.0'
          }
        });

        if (webhookResponse.ok || webhookResponse.status === 403) {
          // 403 is acceptable as it means the webhook exists but requires proper verification
          webhookStatus = 'Accesible';
        } else {
          webhookStatus = `Error ${webhookResponse.status}`;
        }
      } catch (webhookError) {
        console.error('Webhook test error:', webhookError);
        webhookStatus = 'No accesible';
      }
    }

    // Success response
    return new Response(
      JSON.stringify({
        success: true,
        message: `Conexión exitosa con WhatsApp Business API.\n\nPhone Number ID: ${phone_number_id}\nDisplay Name: ${whatsappData.display_phone_number || 'N/A'}\nWebhook: ${webhookStatus}`,
        data: {
          phone_number_id: phone_number_id,
          display_phone_number: whatsappData.display_phone_number,
          verified_name: whatsappData.verified_name,
          webhook_status: webhookStatus
        }
      }),
      {
        status: 200,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );

  } catch (error: any) {
    console.error('Error in test-whatsapp-webhook:', error);
    return new Response(
      JSON.stringify({
        success: false,
        message: `Error al probar la conexión: ${error.message}`
      }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );
  }
});

