ALTER TABLE printer_config ALTER COLUMN user_id TYPE text USING user_id::text;

-- Add a comment to explain the change
COMMENT ON COLUMN printer_config.user_id IS 'User identifier from PIN-based auth system (e.g., admin-1234567890). Not a foreign key to auth.users.';

