-- Create a sequence for order numbers that never resets
-- This ensures order numbers are unique and non-reusable even after deletion
CREATE SEQUENCE IF NOT EXISTS order_number_seq START WITH 1;

-- Drop the old function
DROP FUNCTION IF EXISTS generate_order_number();

-- Create new function that uses sequence for guaranteed uniqueness
CREATE OR REPLACE FUNCTION generate_order_number()
RETURNS text
LANGUAGE plpgsql
AS $$
DECLARE
  new_number text;
  seq_value bigint;
BEGIN
  -- Get next value from sequence (this never reuses values)
  seq_value := nextval('order_number_seq');

  -- Format: YYYYMMDD-NNNNNN (date + 6-digit sequence number)
  new_number := to_char(now(), 'YYYYMMDD') || '-' || lpad(seq_value::text, 6, '0');

  -- Ensure uniqueness (in case of race conditions or manual insertions)
  WHILE EXISTS (SELECT 1 FROM orders WHERE order_number = new_number) LOOP
    seq_value := nextval('order_number_seq');
    new_number := to_char(now(), 'YYYYMMDD') || '-' || lpad(seq_value::text, 6, '0');
  END LOOP;

  RETURN new_number;
END;
$$;

-- Update the sequence to start from the highest existing order number + 1
-- This prevents conflicts with existing orders
DO $$
DECLARE
  max_seq_from_orders bigint;
BEGIN
  -- Extract the highest sequence number from existing order_numbers
  SELECT COALESCE(MAX(
    CASE 
      WHEN order_number ~ '^\d{8}-\d+$' 
      THEN CAST(split_part(order_number, '-', 2) AS bigint)
      ELSE 0
    END
  ), 0) INTO max_seq_from_orders
  FROM orders;

  -- Set sequence to start after the highest existing number
  PERFORM setval('order_number_seq', GREATEST(max_seq_from_orders, 1), true);

  RAISE NOTICE 'Order number sequence initialized to start at %', max_seq_from_orders + 1;
END;
$$;

-- Add comment to document the behavior
COMMENT ON SEQUENCE order_number_seq IS 'Sequence for generating unique, non-reusable order numbers. Never resets, even when orders are deleted.';
COMMENT ON FUNCTION generate_order_number() IS 'Generates unique order numbers in format YYYYMMDD-NNNNNN using a sequence that never reuses values, even after order deletion.';

-- Demonstrate that order numbers are non-reusable
-- 1. Check current sequence value
SELECT last_value AS current_sequence_value FROM pg_sequences WHERE sequencename = 'order_number_seq';

-- Create order_queries table to store customer queries about orders
CREATE TABLE IF NOT EXISTS order_queries (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  order_id UUID NOT NULL REFERENCES orders(id) ON DELETE CASCADE,
  customer_phone TEXT NOT NULL,
  query_text TEXT NOT NULL,
  whatsapp_message_id TEXT,
  created_at TIMESTAMPTZ DEFAULT now()
);

-- Enable RLS
ALTER TABLE order_queries ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Anyone can view order queries" 
  ON order_queries FOR SELECT 
  USING (true);

CREATE POLICY "Anyone can insert order queries" 
  ON order_queries FOR INSERT 
  WITH CHECK (true);

CREATE POLICY "Anyone can update order queries" 
  ON order_queries FOR UPDATE 
  USING (true);

CREATE POLICY "Anyone can delete order queries" 
  ON order_queries FOR DELETE 
  USING (true);

-- Create index for faster lookups
CREATE INDEX IF NOT EXISTS idx_order_queries_order_id ON order_queries(order_id);
CREATE INDEX IF NOT EXISTS idx_order_queries_customer_phone ON order_queries(customer_phone);

