-- Add direction field to order_queries table to distinguish incoming vs outgoing messages
ALTER TABLE order_queries 
ADD COLUMN direction text DEFAULT 'incoming' CHECK (direction IN ('incoming', 'outgoing'));

-- Add comment to explain the field
COMMENT ON COLUMN order_queries.direction IS 'Direction of the message: incoming (from customer) or outgoing (from business to customer)';

-- Update existing queries to be marked as incoming (from customers)
UPDATE order_queries SET direction = 'incoming' WHERE direction IS NULL;

-- Create print_queue table for tracking items that need auto-printing
create table if not exists print_queue (
  id uuid primary key default gen_random_uuid(),
  item_type text not null check (item_type in ('order', 'query')),
  item_id uuid not null,
  status text not null default 'pending' check (status in ('pending', 'printed', 'failed')),
  created_at timestamp with time zone default now(),
  printed_at timestamp with time zone,
  error_message text
);

-- Enable RLS
alter table print_queue enable row level security;

-- Create RLS policies
create policy "Allow all operations on print_queue for authenticated users"
  on print_queue
  for all
  using (true)
  with check (true);

-- Create index for faster queries
create index if not exists idx_print_queue_status on print_queue(status);
create index if not exists idx_print_queue_item on print_queue(item_type, item_id);

-- Drop the existing trigger and function
DROP TRIGGER IF EXISTS trigger_update_customer_totals_on_order ON orders;
DROP TRIGGER IF EXISTS trigger_update_customer_totals_on_payment ON customer_payments;
DROP FUNCTION IF EXISTS update_customer_totals();

-- Create improved function that handles both INSERT and UPDATE on orders
CREATE OR REPLACE FUNCTION update_customer_totals()
RETURNS trigger
LANGUAGE plpgsql
AS $$
DECLARE
  affected_customer_id uuid;
BEGIN
  -- Determine which customer_id to update
  IF TG_OP = 'DELETE' THEN
    affected_customer_id := OLD.customer_id;
  ELSIF TG_OP = 'UPDATE' THEN
    -- Update both old and new customer if customer_id changed
    IF OLD.customer_id IS DISTINCT FROM NEW.customer_id THEN
      -- Update old customer
      IF OLD.customer_id IS NOT NULL THEN
        UPDATE customers
        SET 
          total_debt = (
            SELECT COALESCE(SUM(total_amount), 0)
            FROM orders
            WHERE customer_id = OLD.customer_id
              AND status = 'pending_payment'
          ),
          total_paid = (
            SELECT COALESCE(SUM(amount), 0)
            FROM customer_payments
            WHERE customer_id = OLD.customer_id
          ),
          updated_at = now()
        WHERE id = OLD.customer_id;
      END IF;
    END IF;
    affected_customer_id := NEW.customer_id;
  ELSE -- INSERT
    affected_customer_id := NEW.customer_id;
  END IF;

  -- Update the affected customer
  IF affected_customer_id IS NOT NULL THEN
    UPDATE customers
    SET 
      total_debt = (
        SELECT COALESCE(SUM(total_amount), 0)
        FROM orders
        WHERE customer_id = affected_customer_id
          AND status = 'pending_payment'
      ),
      total_paid = (
        SELECT COALESCE(SUM(amount), 0)
        FROM customer_payments
        WHERE customer_id = affected_customer_id
      ),
      updated_at = now()
    WHERE id = affected_customer_id;
  END IF;

  IF TG_OP = 'DELETE' THEN
    RETURN OLD;
  ELSE
    RETURN NEW;
  END IF;
END;
$$;

-- Recreate triggers
CREATE TRIGGER trigger_update_customer_totals_on_order
  AFTER INSERT OR UPDATE OR DELETE ON orders
  FOR EACH ROW
  EXECUTE FUNCTION update_customer_totals();

CREATE TRIGGER trigger_update_customer_totals_on_payment
  AFTER INSERT OR UPDATE OR DELETE ON customer_payments
  FOR EACH ROW
  EXECUTE FUNCTION update_customer_totals();

