ALTER TABLE orders ADD CONSTRAINT orders_status_check 
  CHECK (status = ANY (ARRAY['pending'::text, 'preparing'::text, 'ready'::text, 'delivered'::text, 'cancelled'::text, 'pending_payment'::text, 'paid'::text]));

-- 2. Create order_payments table to track individual order payments
CREATE TABLE IF NOT EXISTS order_payments (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  order_id uuid NOT NULL REFERENCES orders(id) ON DELETE CASCADE,
  customer_id uuid NOT NULL REFERENCES customers(id) ON DELETE CASCADE,
  amount numeric NOT NULL CHECK (amount > 0),
  payment_date timestamptz DEFAULT now(),
  notes text,
  created_by uuid REFERENCES auth.users(id),
  created_at timestamptz DEFAULT now()
);

-- 3. Enable RLS on order_payments
ALTER TABLE order_payments ENABLE ROW LEVEL SECURITY;

-- 4. Create RLS policies for order_payments (allow anon for PIN-based auth)
CREATE POLICY "Allow anon to insert order_payments" ON order_payments
  FOR INSERT TO anon WITH CHECK (true);

CREATE POLICY "Allow anon to select order_payments" ON order_payments
  FOR SELECT TO anon USING (true);

CREATE POLICY "Allow anon to update order_payments" ON order_payments
  FOR UPDATE TO anon USING (true);

CREATE POLICY "Allow anon to delete order_payments" ON order_payments
  FOR DELETE TO anon USING (true);

-- 5. Create function to update order status when fully paid
CREATE OR REPLACE FUNCTION check_and_update_order_payment_status()
RETURNS TRIGGER AS $$
DECLARE
  order_total numeric;
  order_paid numeric;
BEGIN
  -- Get the order's total amount
  SELECT total_amount INTO order_total
  FROM orders
  WHERE id = NEW.order_id;

  -- Calculate total paid for this order (from order_payments table)
  SELECT COALESCE(SUM(amount), 0) INTO order_paid
  FROM order_payments
  WHERE order_id = NEW.order_id;

  -- Update the order's paid_amount
  UPDATE orders
  SET paid_amount = order_paid
  WHERE id = NEW.order_id;

  -- If fully paid, update status to 'paid'
  IF order_paid >= order_total AND order_total > 0 THEN
    UPDATE orders
    SET status = 'paid'
    WHERE id = NEW.order_id AND status = 'pending_payment';
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- 6. Create trigger for order_payments
DROP TRIGGER IF EXISTS trigger_check_order_payment_status ON order_payments;
CREATE TRIGGER trigger_check_order_payment_status
  AFTER INSERT OR UPDATE OR DELETE ON order_payments
  FOR EACH ROW
  EXECUTE FUNCTION check_and_update_order_payment_status();

-- 7. Create indexes for better performance
CREATE INDEX IF NOT EXISTS idx_order_payments_order_id ON order_payments(order_id);
CREATE INDEX IF NOT EXISTS idx_order_payments_customer_id ON order_payments(customer_id);
CREATE INDEX IF NOT EXISTS idx_orders_status ON orders(status);

-- 8. Add comment to order_payments table
COMMENT ON TABLE order_payments IS 'Tracks individual payments made towards specific orders. Used for partial payments and tracking payment history per order.';

SELECT pg_get_functiondef(oid) as function_definition
FROM pg_proc
WHERE proname = 'update_customer_totals';

-- Update the trigger function to handle DELETE operations properly
CREATE OR REPLACE FUNCTION check_and_update_order_payment_status()
RETURNS trigger
LANGUAGE plpgsql
AS $$
DECLARE
  target_order_id uuid;
  order_total numeric;
  order_paid numeric;
BEGIN
  -- Determine which order_id to use based on operation
  IF TG_OP = 'DELETE' THEN
    target_order_id := OLD.order_id;
  ELSE
    target_order_id := NEW.order_id;
  END IF;

  -- Get the order's total amount
  SELECT total_amount INTO order_total
  FROM orders
  WHERE id = target_order_id;

  -- Calculate total paid for this order (from order_payments table)
  SELECT COALESCE(SUM(amount), 0) INTO order_paid
  FROM order_payments
  WHERE order_id = target_order_id;

  -- Update the order's paid_amount
  UPDATE orders
  SET paid_amount = order_paid
  WHERE id = target_order_id;

  -- If fully paid, update status to 'paid'
  IF order_paid >= order_total AND order_total > 0 THEN
    UPDATE orders
    SET status = 'paid'
    WHERE id = target_order_id AND status = 'pending_payment';
  ELSIF order_paid < order_total THEN
    -- If not fully paid anymore (e.g., payment was deleted), revert to pending_payment
    UPDATE orders
    SET status = 'pending_payment'
    WHERE id = target_order_id AND status = 'paid';
  END IF;

  -- Return appropriate value based on operation
  IF TG_OP = 'DELETE' THEN
    RETURN OLD;
  ELSE
    RETURN NEW;
  END IF;
END;
$$;

-- Function to automatically distribute customer payments to pending orders
CREATE OR REPLACE FUNCTION distribute_customer_payment_to_orders()
RETURNS trigger
LANGUAGE plpgsql
AS $$
DECLARE
  remaining_amount numeric;
  order_record RECORD;
  order_pending numeric;
  payment_to_apply numeric;
BEGIN
  -- Only process if this is a customer_payment (not an order_payment)
  -- We can tell by checking if there's no order_id in the payment
  -- Since customer_payments table doesn't have order_id, this function
  -- will distribute the payment across all pending orders

  remaining_amount := NEW.amount;

  -- Get all pending_payment orders for this customer, ordered by creation date (oldest first)
  FOR order_record IN 
    SELECT id, total_amount, paid_amount
    FROM orders
    WHERE customer_id = NEW.customer_id
      AND status = 'pending_payment'
    ORDER BY created_at ASC
  LOOP
    -- Calculate how much is pending for this order
    order_pending := order_record.total_amount - order_record.paid_amount;

    -- If there's still amount to distribute and this order has pending amount
    IF remaining_amount > 0 AND order_pending > 0 THEN
      -- Determine how much to apply to this order
      payment_to_apply := LEAST(remaining_amount, order_pending);

      -- Create an order_payment record
      INSERT INTO order_payments (order_id, customer_id, amount, notes, created_by, payment_date)
      VALUES (
        order_record.id,
        NEW.customer_id,
        payment_to_apply,
        COALESCE(NEW.notes, '') || ' (Auto-distribuido desde abono a cuenta)',
        NEW.created_by,
        NEW.payment_date
      );

      -- Reduce the remaining amount
      remaining_amount := remaining_amount - payment_to_apply;
    END IF;

    -- Exit loop if we've distributed all the payment
    EXIT WHEN remaining_amount <= 0;
  END LOOP;

  RETURN NEW;
END;
$$;

-- Create trigger for customer_payments
DROP TRIGGER IF EXISTS trigger_distribute_customer_payment ON customer_payments;
CREATE TRIGGER trigger_distribute_customer_payment
  AFTER INSERT ON customer_payments
  FOR EACH ROW
  EXECUTE FUNCTION distribute_customer_payment_to_orders();

