import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface WhatsAppMessage {
  from: string;
  id: string;
  timestamp: string;
  text: {
    body: string;
  };
  type: string;
}

interface WhatsAppWebhookEntry {
  changes: Array<{
    value: {
      messages?: WhatsAppMessage[];
      metadata: {
        phone_number_id: string;
      };
    };
  }>;
}

interface ParsedOrder {
  customerName: string;
  customerPhone: string;
  items: Array<{
    product_name: string;
    quantity: number;
    unit_price: number;
    notes?: string;
  }>;
  notes?: string;
}

function parseOrderFromMessage(message: string, phone: string): ParsedOrder | null {
  console.log('Parsing message:', message);

  // Simple parsing logic - can be enhanced with AI/NLP
  const lines = message.split('\n').filter(line => line.trim());

  if (lines.length === 0) return null;

  const items: ParsedOrder['items'] = [];
  let customerName = '';
  let notes = '';

  // Try to extract customer name from first line or use phone
  customerName = lines[0].trim() || phone;

  // Parse items - looking for patterns like:
  // - 2 Pizza Margherita $10
  // - 1x Hamburguesa $8
  // - Coca Cola x3 $2
  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();

    // Skip empty lines
    if (!line) continue;

    // Try to match quantity, product, and price
    const patterns = [
      /^(\d+)\s*x?\s+(.+?)\s+\$?([\d.]+)$/i,
      /^(\d+)\s+(.+?)\s+\$?([\d.]+)$/i,
      /^(.+?)\s+x\s*(\d+)\s+\$?([\d.]+)$/i,
    ];

    let matched = false;
    for (const pattern of patterns) {
      const match = line.match(pattern);
      if (match) {
        const quantity = parseInt(match[1]);
        const productName = match[2].trim();
        const price = parseFloat(match[3]);

        if (!isNaN(quantity) && !isNaN(price) && productName) {
          items.push({
            product_name: productName,
            quantity,
            unit_price: price,
          });
          matched = true;
          break;
        }
      }
    }

    // If no pattern matched, treat as notes
    if (!matched && line.toLowerCase().includes('nota')) {
      notes += line + ' ';
    }
  }

  // If no items found, create a generic item with the full message
  if (items.length === 0) {
    items.push({
      product_name: 'Pedido por WhatsApp',
      quantity: 1,
      unit_price: 0,
      notes: message,
    });
  }

  return {
    customerName,
    customerPhone: phone,
    items,
    notes: notes.trim() || undefined,
  };
}

async function sendWhatsAppMessage(
  phoneNumberId: string,
  accessToken: string,
  to: string,
  message: string
) {
  const url = `https://graph.facebook.com/v17.0/${phoneNumberId}/messages`;

  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      messaging_product: 'whatsapp',
      to,
      text: { body: message },
    }),
  });

  if (!response.ok) {
    const error = await response.text();
    console.error('Failed to send WhatsApp message:', error);
    throw new Error(`Failed to send message: ${error}`);
  }

  return response.json();
}

serve(async (req) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // GET request - webhook verification
    if (req.method === 'GET') {
      const url = new URL(req.url);
      const mode = url.searchParams.get('hub.mode');
      const token = url.searchParams.get('hub.verify_token');
      const challenge = url.searchParams.get('hub.challenge');

      console.log('Webhook verification request:', { mode, token });

      // Get verify token from database
      const { data: config } = await supabase
        .from('whatsapp_config')
        .select('verify_token')
        .single();

      if (mode === 'subscribe' && token === config?.verify_token) {
        console.log('Webhook verified successfully');
        return new Response(challenge, {
          headers: { ...corsHeaders, 'Content-Type': 'text/plain' },
        });
      }

      return new Response('Forbidden', { status: 403, headers: corsHeaders });
    }

    // POST request - incoming message
    if (req.method === 'POST') {
      const body = await req.json();
      console.log('Received webhook:', JSON.stringify(body, null, 2));

      // Get WhatsApp config
      const { data: config } = await supabase
        .from('whatsapp_config')
        .select('*')
        .eq('is_active', true)
        .single();

      if (!config) {
        console.log('WhatsApp integration not configured or inactive');
        return new Response(JSON.stringify({ success: false, error: 'Not configured' }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        });
      }

      // Process webhook entries
      const entry: WhatsAppWebhookEntry = body.entry?.[0];
      if (!entry) {
        return new Response(JSON.stringify({ success: true }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        });
      }

      const changes = entry.changes?.[0];
      const messages = changes?.value?.messages;

      if (!messages || messages.length === 0) {
        return new Response(JSON.stringify({ success: true }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        });
      }

      // Process each message
      for (const message of messages) {
        if (message.type !== 'text') continue;

        const messageText = message.text.body;
        const customerPhone = message.from;
        const messageId = message.id;

        console.log('Processing message from:', customerPhone);

        // Parse order from message
        const parsedOrder = parseOrderFromMessage(messageText, customerPhone);

        if (!parsedOrder) {
          console.log('Could not parse order from message');
          continue;
        }

        // Create order in database
        const { data: order, error: orderError } = await supabase
          .from('orders')
          .insert({
            customer_name: parsedOrder.customerName,
            customer_phone: parsedOrder.customerPhone,
            status: 'pending',
            source: 'whatsapp',
            whatsapp_message_id: messageId,
            notes: parsedOrder.notes,
          })
          .select()
          .single();

        if (orderError) {
          console.error('Error creating order:', orderError);
          continue;
        }

        console.log('Created order:', order.id);

        // Create order items
        const orderItems = parsedOrder.items.map(item => ({
          order_id: order.id,
          product_name: item.product_name,
          quantity: item.quantity,
          unit_price: item.unit_price,
          notes: item.notes,
        }));

        const { error: itemsError } = await supabase
          .from('order_items')
          .insert(orderItems);

        if (itemsError) {
          console.error('Error creating order items:', itemsError);
        }

        // Send auto-reply if enabled
        if (config.auto_reply_enabled && config.access_token && config.phone_number_id) {
          try {
            const replyMessage = config.auto_reply_message.replace(
              '{order_number}',
              order.order_number
            );

            await sendWhatsAppMessage(
              config.phone_number_id,
              config.access_token,
              customerPhone,
              replyMessage
            );

            console.log('Sent auto-reply to:', customerPhone);
          } catch (error) {
            console.error('Error sending auto-reply:', error);
          }
        }
      }

      return new Response(JSON.stringify({ success: true }), {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      });
    }

    return new Response('Method not allowed', { status: 405, headers: corsHeaders });
  } catch (error) {
    console.error('Error processing webhook:', error);
    return new Response(
      JSON.stringify({ success: false, error: error.message }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

